"use client"

import { useState } from "react"

import { CharacterProvider, useCharacter } from "./character-context"
import { useCharacters } from "@/hooks/use-characters"
import { toast } from "sonner"
import { useEffect } from "react"
import { supabase } from "@/lib/supabase"
import { generateCharacterPDF } from "@/lib/pdf-service"
import { WizardSteps } from "./wizard-steps"
import { RaceStep } from "./steps/race-step"
import { ClassStep } from "./steps/class-step"
import { AbilitiesStep } from "./steps/abilities-step"
import { BackgroundStep } from "./steps/background-step"
import { DetailsStep } from "./steps/details-step"
import { EquipmentStep } from "./steps/equipment-step"
import { SummaryStep } from "./steps/summary-step"
import { Button } from "@/components/ui/button"
import { Card, CardContent } from "@/components/ui/card"
import { ArrowLeft, ArrowRight, Users, FileText } from "lucide-react"

import { SpellsStep } from "./steps/spells-step"

const casterClasses = ['bard', 'cleric', 'druid', 'sorcerer', 'warlock', 'wizard']

function WizardContent() {
  const { character, currentStep, setCurrentStep } = useCharacter()
  const { saveCharacter } = useCharacters()
  const [isSaving, setIsSaving] = useState(false)
  const [user, setUser] = useState<any>(null)

  useEffect(() => {
    supabase.auth.getUser().then(({ data: { user } }) => {
      setUser(user)
    })
  }, [])

  const isCaster = character.class && casterClasses.includes(character.class.id)

  // Definici├│n din├ímica de pasos
  const allStepsDefinition = [
    { title: "Raza", component: RaceStep, validate: () => character.race !== null },
    { title: "Clase", component: ClassStep, validate: () => character.class !== null },
    ...(isCaster ? [{ title: "Hechizos", component: SpellsStep, validate: () => true }] : []),
    { title: "Atributos", component: AbilitiesStep, validate: () => true },
    { title: "Trasfondo", component: BackgroundStep, validate: () => character.background !== null },
    { title: "Detalles", component: DetailsStep, validate: () => character.name.trim() !== "" },
    { title: "Equipo", component: EquipmentStep, validate: () => true },
    { title: "Resumen", component: SummaryStep, validate: () => true },
  ]

  const steps = allStepsDefinition.map((step, index) => ({
    id: index,
    title: step.title,
    component: step.component,
    validate: step.validate
  }))

  const canProceed = () => {
    return steps[currentStep]?.validate() ?? true
  }

  const handleNext = async () => {
    if (currentStep === steps.length - 1) {
      setIsSaving(true)
      const { error } = await saveCharacter({
        nombre: character.name,
        raza_id: character.race?.id,
        clase_id: character.class?.id,
        nivel: character.level,
        estadisticas: { 
            abilities: character.abilities, 
            skills: character.skills, 
            spells: character.spells,
            hitDie: character.class?.hitDie, 
            speed: character.race?.speed 
        },
        biografia: { 
            alignment: character.alignment,
            personalityTraits: character.personalityTraits, 
            ideals: character.ideals, 
            bonds: character.bonds, 
            flaws: character.flaws,
            background: character.background?.id 
        }
      })
      setIsSaving(false)

      if (error) {
        toast.error("Error al guardar personaje", { description: typeof error === 'string' ? error : error.message })
      } else {
        toast.success("┬íPersonaje guardado!", { description: "Tu heroe ha sido inmortalizado en la taberna." })
      }
      return
    }

    if (currentStep < steps.length - 1 && canProceed()) {
      setCurrentStep(currentStep + 1)
    }
  }

  const handleBack = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1)
    }
  }

  const StepComponent = steps[currentStep]?.component || (() => null)

  return (
    <div className="max-w-5xl mx-auto">
      {/* Header */}
      <div className="text-center mb-12">
        <div className="inline-flex items-center justify-center h-20 w-20 rounded-none bg-[#242528] text-white mb-6 border-b-4 border-[#EE8600] shadow-xl">
          <Users className="h-10 w-10" />
        </div>
        <h1 className="font-heading text-5xl lg:text-7xl font-bold tracking-tight text-[#242528] uppercase leading-none">
          CREADOR DE PERSONAJES
        </h1>
        <p className="mt-4 text-xl text-muted-foreground font-sans italic">
          "Forja tu h├⌐roe paso a paso y desciende a la aventura."
        </p>
        <div className="mt-8">
            <Button 
                variant="outline" 
                size="sm" 
                className="gap-2 border-border text-gray-500 hover:text-primary hover:bg-gray-50 font-black uppercase tracking-widest text-[10px] h-10 px-6" 
                onClick={() => generateCharacterPDF(character, user?.email, true)}
            >
                <FileText className="h-4 w-4 text-primary" />
                Descargar Hoja en Blanco
            </Button>
        </div>
      </div>

      {/* Progress Steps */}
      <WizardSteps
        steps={steps.map(s => ({ id: s.id, title: s.title }))}
        currentStep={currentStep}
        onStepClick={(step) => step <= currentStep && setCurrentStep(step)}
      />

      {/* Step Content */}
      <Card className="high-contrast-card">
        <CardContent className="p-8 lg:p-12">
          <StepComponent />
        </CardContent>
      </Card>

      {/* Navigation */}
      <div className="mt-10 flex items-center justify-between border-t-4 border-accent-gold pt-8">
        <Button
          variant="outline"
          onClick={handleBack}
          disabled={currentStep === 0 || isSaving}
          className="border-[#E1E1E1] bg-white text-[#242528] hover:bg-[#F9F9F9] font-bold uppercase tracking-widest text-xs h-14 px-10 rounded-none"
        >
          <ArrowLeft className="mr-3 h-5 w-5 text-[#EE8600]" />
          Anterior
        </Button>
        <div className="text-xs font-bold uppercase tracking-[0.3em] text-[#242528]/40">
          PASO {currentStep + 1} DE {steps.length}
        </div>
        <Button
          onClick={handleNext}
          disabled={!canProceed() || isSaving}
          className="bg-[#242528] text-white hover:bg-black min-w-[200px] h-14 rounded-none font-bold uppercase tracking-widest text-xs shadow-lg"
        >
          {isSaving ? (
            <>
              <span className="mr-3 h-5 w-5 animate-spin rounded-full border-2 border-white border-t-transparent" />
              Guardando...
            </>
          ) : currentStep === steps.length - 1 ? (
            <>
              Guardar Personaje
              <Users className="ml-3 h-5 w-5" />
            </>
          ) : (
            <>
              Siguiente
              <ArrowRight className="ml-3 h-5 w-5" />
            </>
          )}
        </Button>
      </div>
    </div>
  )
}

export function CharacterCreatorWizard() {
  return (
    <CharacterProvider>
      <WizardContent />
    </CharacterProvider>
  )
}

"use client"

import { abilities, calculateModifier, formatModifier } from "@/lib/character-data"
import { useCharacter } from "../character-context"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { Slider } from "@/components/ui/slider"
import { Dices, RotateCcw, Sparkles, ChevronUp, ChevronDown } from "lucide-react"
import { cn } from "@/lib/utils"

export function AbilitiesStep() {
  const { character, updateCharacter } = useCharacter()

  const pointsUsed = Object.values(character.abilities).reduce((sum, val) => {
    if (val <= 13) return sum + (val - 8)
    if (val === 14) return sum + 7
    if (val === 15) return sum + 9
    return sum
  }, 0)

  const maxPoints = 27
  const remainingPoints = maxPoints - pointsUsed

  const handleAbilityChange = (abilityId: string, value: number) => {
    updateCharacter({
      abilities: {
        ...character.abilities,
        [abilityId]: value,
      },
    })
  }

  const rollStats = () => {
    const roll4d6DropLowest = () => {
      const rolls = Array.from({ length: 4 }, () => Math.floor(Math.random() * 6) + 1)
      rolls.sort((a, b) => b - a)
      return rolls.slice(0, 3).reduce((sum, val) => sum + val, 0)
    }

    const newAbilities = {
      str: roll4d6DropLowest(),
      dex: roll4d6DropLowest(),
      con: roll4d6DropLowest(),
      int: roll4d6DropLowest(),
      wis: roll4d6DropLowest(),
      cha: roll4d6DropLowest(),
    }

    updateCharacter({ abilities: newAbilities })
  }

  const resetStats = () => {
    updateCharacter({
      abilities: { str: 10, dex: 10, con: 10, int: 10, wis: 10, cha: 10 },
    })
  }

  const racialBonuses = character.race?.abilityBonuses || {}

  return (
    <div className="space-y-10 pb-10">
      <div className="space-y-4">
        <h2 className="font-heading text-4xl font-bold text-[#242528] uppercase tracking-tight">3. Atributos</h2>
        <div className="h-[3px] w-32 bg-[#EE8600]" />
        <p className="text-lg text-[#242528]/60 font-sans italic">
          "Define el potencial f├¡sico y mental de tu aventurero."
        </p>
      </div>

      {/* Control Bar - D&D Beyond Style */}
      <div className="bg-white border border-[#E1E1E1] p-6 flex flex-wrap items-center justify-between gap-6 shadow-sm">
        <div className="flex gap-4">
          <Button 
            onClick={rollStats} 
            className="h-12 px-6 bg-[#242528] hover:bg-[#EE8600] text-white rounded-none font-bold uppercase tracking-widest text-[10px] transition-colors"
          >
            <Dices className="mr-3 h-4 w-4" />
            Tirar Dados (4d6)
          </Button>
          <Button 
            onClick={resetStats} 
            variant="outline"
            className="h-12 px-6 border-[#E1E1E1] text-[#242528] rounded-none font-bold uppercase tracking-widest text-[10px] hover:bg-gray-50"
          >
            <RotateCcw className="mr-3 h-4 w-4 text-[#EE8600]" />
            Reiniciar
          </Button>
        </div>

        <div className="flex items-center gap-4 border-l border-[#E1E1E1] pl-6">
          <span className="text-[10px] font-bold uppercase tracking-widest text-[#242528]/40">Puntos Disponibles</span>
          <div className={cn(
            "text-3xl font-heading font-bold",
            remainingPoints < 0 ? "text-red-500" : "text-[#242528]"
          )}>
            {remainingPoints}
          </div>
        </div>
      </div>

      {/* Ability Scores Grid */}
      <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {abilities.map((ability) => {
          const baseScore = character.abilities[ability.id as keyof typeof character.abilities]
          const racialBonus = racialBonuses[ability.id] || 0
          const totalScore = baseScore + racialBonus
          const modifier = calculateModifier(totalScore)

          return (
            <div key={ability.id} className="border border-[#E1E1E1] bg-white group hover:border-[#EE8600] transition-colors shadow-sm overflow-hidden">
               {/* Accent top bar */}
               <div className="h-1 w-full bg-[#242528] group-hover:bg-[#EE8600] transition-colors" />

               <div className="p-8 space-y-8">
                  <div className="flex justify-between items-start">
                    <div className="space-y-1">
                      <h3 className="font-heading text-3xl font-bold text-[#242528] uppercase tracking-tight">{ability.name}</h3>
                      <span className="text-[10px] font-bold text-[#242528]/30 uppercase tracking-[0.2em]">{ability.id}</span>
                    </div>
                    {/* Big Mod Circle style - La Taberna themed */}
                    <div className="w-20 h-20 rounded-none border-2 border-[#242528] flex flex-col items-center justify-center bg-[#F9F9F9] shadow-[4px_4px_0px_0px_rgba(36,37,40,1)]">
                       <span className="text-[10px] font-bold text-[#242528]/40 uppercase leading-none mb-1">MOD</span>
                       <span className="text-3xl font-heading font-bold text-[#242528]">
                         {formatModifier(modifier)}
                       </span>
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4 items-center">
                    <div className="space-y-1">
                       <span className="text-[9px] font-bold uppercase tracking-[0.2em] text-[#242528]/40">Valor Total</span>
                       <div className="text-4xl font-heading font-bold text-[#EE8600]">{totalScore}</div>
                    </div>
                    {/* Stepper controls */}
                    <div className="flex border border-[#E1E1E1] h-14">
                       <button 
                        onClick={() => handleAbilityChange(ability.id, Math.max(8, baseScore - 1))}
                        className="flex-1 flex items-center justify-center hover:bg-[#F9F9F9] border-r border-[#E1E1E1] transition-colors"
                       >
                         <ChevronDown className="h-4 w-4 text-[#242528]" />
                       </button>
                       <div className="w-12 flex items-center justify-center font-bold text-[#242528] bg-white">
                         {baseScore}
                       </div>
                       <button 
                        onClick={() => handleAbilityChange(ability.id, Math.min(15, baseScore + 1))}
                        className="flex-1 flex items-center justify-center hover:bg-[#F9F9F9] transition-colors"
                       >
                         <ChevronUp className="h-4 w-4 text-[#242528]" />
                       </button>
                    </div>
                  </div>

                  {/* Racial Bonus pill */}
                  <div className="pt-4 border-t border-[#E1E1E1] flex items-center justify-between">
                     <span className="text-[9px] font-bold uppercase tracking-widest text-[#242528]/30 italic">Puntos Base: {baseScore}</span>
                     {racialBonus > 0 && (
                        <div className="flex items-center gap-2 bg-[#242528] text-white px-3 py-1 text-[9px] font-bold uppercase tracking-widest">
                          +{racialBonus} {character.race?.name}
                        </div>
                     )}
                  </div>
               </div>
            </div>
          )
        })}
      </div>

      {/* Racial Summary Footer */}
      {character.race && (
        <div className="p-10 border border-[#E1E1E1] bg-[#F9F9F9] space-y-6">
           <h4 className="font-heading text-xl font-bold uppercase tracking-tight text-[#242528] flex items-center gap-3">
             <Sparkles className="h-5 w-5 text-[#EE8600]" /> Ajustes de Linaje
           </h4>
           <div className="flex flex-wrap gap-4">
              {Object.entries(character.race.abilityBonuses).map(([ability, bonus]) => (
                <div key={ability} className="flex items-center gap-3 bg-white border border-[#E1E1E1] p-4 min-w-[160px]">
                   <span className="w-1.5 h-1.5 bg-[#EE8600] rounded-full" />
                   <span className="text-[10px] font-bold uppercase tracking-widest text-[#242528]/40 shrink-0">
                     {abilities.find(a => a.id === ability)?.name}
                   </span>
                   <span className="text-2xl font-heading font-bold text-[#242528] ml-auto">+{bonus}</span>
                </div>
              ))}
           </div>
        </div>
      )}
    </div>
  )
}

"use client"

import { useCharacter } from "../character-context"
import { Checkbox } from "@/components/ui/checkbox"
import { Label } from "@/components/ui/label"
import { ScrollArea } from "@/components/ui/scroll-area"
import { Badge } from "@/components/ui/badge"
import { Shield, Sword, Package, FlaskConical, Hammer } from "lucide-react"

const starterItems = [
  { id: "exploracion", category: "Exploraci├│n", icon: Package, items: ["Mochila", "Saco de dormir", "Caja de raciones (10)", "Odre", "Antorchas (10)", "Cuerda de c├í├▒amo (50 pies)"] },
  { id: "armas", category: "Armas", icon: Sword, items: ["Espada larga", "Arco corto", "Daga", "Hacha de mano", "Martillo de guerra", "Flechas (20)"] },
  { id: "armaduras", category: "Armaduras", icon: Shield, items: ["Armadura de cuero", "Cota de malla", "Escudo"] },
  { id: "utilidad", category: "Utilidad", icon: FlaskConical, items: ["Poci├│n de curaci├│n", "Caja de herramientas de ladr├│n", "S├¡mbolo sagrado", "Libro de hechizos", "Foco arcano"] },
]

export function EquipmentStep() {
  const { character, updateCharacter } = useCharacter()

  const toggleItem = (item: string) => {
    const currentItems = character.items || []
    if (currentItems.includes(item)) {
      updateCharacter({ items: currentItems.filter((i) => i !== item) })
    } else {
      updateCharacter({ items: [...currentItems, item] })
    }
  }

  return (
    <div className="space-y-10 pb-10">
      <div className="space-y-4">
        <h2 className="font-heading text-4xl font-bold text-[#242528] uppercase tracking-tight">4. Equipo de Aventurero</h2>
        <div className="h-[3px] w-32 bg-[#EE8600]" />
        <p className="text-lg text-[#242528]/60 font-sans italic">
          "Un h├⌐roe es tan bueno como las herramientas que lleva."
        </p>
      </div>

      <div className="grid gap-6 md:grid-cols-2">
        {starterItems.map((group) => (
          <div key={group.id} className="p-6 border border-[#E1E1E1] bg-white shadow-sm flex flex-col h-full">
            <div className="flex items-center gap-3 mb-6 border-b border-[#E1E1E1] pb-4">
              <div className="p-2 bg-[#F9F9F9] border border-[#E1E1E1]">
                <group.icon className="w-5 h-5 text-[#EE8600]" />
              </div>
              <h3 className="font-heading text-xl font-bold uppercase tracking-tight text-[#242528]">{group.category}</h3>
            </div>
            
            <ScrollArea className="flex-grow">
              <div className="space-y-3">
                {group.items.map((item) => {
                  const isSelected = character.items?.includes(item)
                  return (
                    <div 
                      key={item} 
                      className={`flex items-center space-x-3 p-3 transition-colors cursor-pointer border ${isSelected ? 'border-[#EE8600] bg-[#EE8600]/5' : 'border-transparent hover:bg-[#F9F9F9]'}`}
                      onClick={() => toggleItem(item)}
                    >
                      <Checkbox 
                        id={item} 
                        checked={isSelected}
                        className="rounded-none border-[#E1E1E1] data-[state=checked]:bg-[#EE8600] data-[state=checked]:border-[#EE8600]"
                      />
                      <label
                        htmlFor={item}
                        className="text-sm font-medium leading-none cursor-pointer flex-grow"
                      >
                        {item}
                      </label>
                      {isSelected && <Badge className="bg-[#EE8600] text-white text-[10px] rounded-none border-none">A├▒adido</Badge>}
                    </div>
                  )
                })}
              </div>
            </ScrollArea>
          </div>
        ))}
      </div>

      <div className="p-6 border border-[#242528] bg-[#F9F9F9] shadow-[6px_6px_0px_0px_rgba(36,37,40,1)]">
        <h4 className="font-heading text-sm font-bold uppercase tracking-widest text-[#242528]/40 mb-3">Resumen de Carga</h4>
        <div className="flex flex-wrap gap-2">
          {character.items?.length === 0 ? (
            <p className="text-sm text-[#242528]/40 italic">No has seleccionado ning├║n objeto a├║n...</p>
          ) : (
            character.items?.map((item) => (
              <Badge key={item} variant="outline" className="rounded-none border-[#E1E1E1] bg-white text-[#242528] px-3 py-1">
                {item}
              </Badge>
            ))
          )}
        </div>
      </div>
    </div>
  )
}

