export function SpellsStep() {
  const { character, updateCharacter } = useCharacter()
  const spells = character.class ? classSpells[character.class.id] : []
  
  if (!spells || spells.length === 0) {
    return (
      <div className="text-center py-20 border border-dashed border-[#E1E1E1] space-y-4 bg-[#F9F9F9]">
        <Zap className="mx-auto h-12 w-12 text-[#242528]/10" />
        <p className="text-[10px] font-bold uppercase tracking-[0.3em] text-[#242528]/40">Tu clase no selecciona hechizos a nivel 1</p>
      </div>
    )
  }

  const selectedSpells = character.spells || []

  const toggleSpell = (spellName: string) => {
    if (selectedSpells.includes(spellName)) {
      updateCharacter({ spells: selectedSpells.filter(s => s !== spellName) })
    } else {
      if (selectedSpells.length < 4) {
         updateCharacter({ spells: [...selectedSpells, spellName] })
      }
    }
  }

  return (
    <div className="space-y-10 pb-10">
      <div className="space-y-4">
        <h2 className="font-heading text-4xl font-bold text-[#242528] uppercase tracking-tight">Selecci├│n de Hechizos</h2>
        <div className="h-[3px] w-32 bg-[#EE8600]" />
        <p className="text-lg text-[#242528]/60 font-sans italic">
          "Elige los conjuros que has preparado para tu aventura (M├íx 4)."
        </p>
      </div>

      {/* Spells Grid - Clean Table-like cards */}
      <div className="grid gap-6 sm:grid-cols-2">
        {spells.map((spell) => {
          const isSelected = selectedSpells.includes(spell.name)
          return (
            <div
              key={spell.name}
              onClick={() => toggleSpell(spell.name)}
              className={cn(
                "group relative bg-white border transition-all duration-300 cursor-pointer overflow-hidden p-8 flex flex-col gap-4",
                isSelected
                  ? "border-[#EE8600] shadow-xl ring-1 ring-[#EE8600]"
                  : "border-[#E1E1E1] hover:border-[#EE8600] shadow-sm"
              )}
            >
              <div className={cn(
                "absolute top-0 left-0 w-full h-1 transition-colors",
                isSelected ? "bg-[#EE8600]" : "bg-[#242528] group-hover:bg-[#EE8600]"
              )} />

              <div className="flex justify-between items-start">
                 <div className="space-y-2">
                    <h3 className="font-heading text-2xl font-bold uppercase text-[#242528] tracking-tight group-hover:text-[#EE8600] transition-colors line-clamp-1">
                      {spell.name}
                    </h3>
                    <Badge variant="outline" className="border-[#242528] text-[#242528] text-[9px] font-bold uppercase py-0 rounded-none bg-[#F9F9F9]">
                      {spell.level === 0 ? "Truco" : `Nivel ${spell.level}`}
                    </Badge>
                 </div>
                 {isSelected && <Sparkles className="h-5 w-5 text-[#EE8600] animate-pulse shrink-0" />}
              </div>

              <p className="text-sm text-[#242528]/70 font-sans leading-relaxed italic border-l-2 border-[#E1E1E1] pl-4">
                "{spell.desc}"
              </p>

              <div className="pt-4 mt-auto">
                 <div className={cn(
                   "flex items-center justify-between py-3 border-t border-[#E1E1E1] transition-colors",
                   isSelected ? "text-[#EE8600]" : "text-[#242528] group-hover:text-[#EE8600]"
                 )}>
                   <span className="text-[10px] font-bold uppercase tracking-[0.2em]">{isSelected ? "Seleccionado" : "Elegir Hechizo"}</span>
                   <ChevronRight className="h-4 w-4" />
                 </div>
              </div>
            </div>
          )
        })}
      </div>

      {/* Selected Counter Footer */}
      <div className="p-8 border-t-4 border-[#EE8600] bg-[#242528] text-white flex justify-between items-center">
         <span className="text-[10px] font-bold uppercase tracking-widest text-[#EE8600]">Hechizos Preparados</span>
         <div className="flex items-center gap-4">
            <div className="flex gap-2">
               {[1, 2, 3, 4].map(idx => (
                 <div key={idx} className={cn(
                   "h-3 w-3 rounded-none border border-white/20 transition-colors",
                   selectedSpells.length >= idx ? "bg-[#EE8600]" : "bg-transparent"
                 )} />
               ))}
            </div>
            <span className="text-2xl font-heading font-bold">{selectedSpells.length}/4</span>
         </div>
      </div>
    </div>
  )
}

"use client"

import { useState, useMemo } from "react"
import { useCart } from "@/components/tienda/cart-context"
import { ProductCard } from "@/components/tienda/product-card"
import { StoreFilters } from "@/components/tienda/store-filters"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Search, Package, User } from "lucide-react"
import { Producto } from "@/lib/shop-types"

interface TiendaClientProps {
  initialProducts: Producto[]
  user: any // Ajustar tipo si tenemos definicion de Usuario de Supabase
}

export function TiendaClient({ initialProducts, user }: TiendaClientProps) {
  const { addToCart } = useCart()
  const [searchTerm, setSearchTerm] = useState("")
  const [selectedCategory, setSelectedCategory] = useState<string>("all")

  // L├│gica de filtrado memorizada
  const filteredProducts = useMemo(() => {
    return initialProducts.filter(product => {
      const matchesSearch = product.Nombre.toLowerCase().includes(searchTerm.toLowerCase()) || 
                            (product.Descripcion && product.Descripcion.toLowerCase().includes(searchTerm.toLowerCase()))
      
      const cat = product.Categoria ? product.Categoria.toLowerCase() : ""
      const matchesCategory = selectedCategory === "all" ? true : cat === selectedCategory

      return matchesSearch && matchesCategory
    })
  }, [initialProducts, searchTerm, selectedCategory])

  return (
    <div className="flex min-h-screen flex-col bg-background">
      <main className="flex-1 py-16 lg:py-24 relative overflow-hidden bg-background">
        {/* Background Effects */}
        <div className="absolute top-0 inset-x-0 h-[600px] bg-gradient-to-b from-muted/50 to-transparent -z-10" />
        
        <div className="container mx-auto px-4 max-w-7xl relative z-10">
            {/* Header */}
            <div className="flex flex-col md:flex-row justify-between items-start md:items-end gap-10 mb-12 border-b-4 border-[#EE8600] pb-8">
                <div className="space-y-4">
                   <div className="inline-flex items-center gap-2 px-3 py-1 border border-[#EE8600]/30 bg-[#EE8600]/10 text-[#EE8600] text-[10px] font-black uppercase tracking-[0.2em]">
                        Mercado de Aventureros
                   </div>
                   <h1 className="font-heading font-black text-5xl lg:text-7xl uppercase tracking-tighter text-[#242528] leading-none">
                        EL MERCADO
                   </h1>
                   <p className="text-[#242528]/60 text-lg max-w-2xl leading-relaxed font-sans italic">
                        "Equ├¡pate para tu pr├│xima aventura con nuestra selecci├│n de artefactos, forjados en la fragua de la experiencia."
                   </p>
                </div>
                
                {user && (
                    <Button variant="outline" className="border-[#E1E1E1] text-[#242528] hover:bg-[#F9F9F9] hover:text-[#EE8600] rounded-none font-bold uppercase text-[10px] tracking-[0.2em] px-6 h-12 transition-all" onClick={() => window.location.href = '/perfil'}>
                        <User className="h-4 w-4 mr-2" />
                        Mi Cuenta
                    </Button>
                )}
            </div>

            {/* Toolbar */}
            <div className="flex flex-col md:flex-row gap-8 mb-16 items-center">
                <div className="relative flex-1 group w-full">
                    <Search className="absolute left-4 top-1/2 -translate-y-1/2 h-5 w-5 text-[#242528]/40 group-focus-within:text-[#EE8600] transition-colors" />
                    <Input 
                        placeholder="Buscar artefactos..." 
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="pl-12 h-14 bg-white border border-[#E1E1E1] focus:border-[#EE8600] text-lg font-sans rounded-none placeholder:text-[#242528]/30 text-[#242528] transition-all"
                    />
                </div>
                <div className="w-full md:w-auto overflow-x-auto pb-2 md:pb-0">
                    <StoreFilters selectedCategory={selectedCategory} onCategoryChange={setSelectedCategory} />
                </div>
            </div>

            {/* Grid */}
            {filteredProducts.length === 0 ? (
                <div className="text-center py-32 bg-[#F9F9F9] border border-dashed border-[#E1E1E1]">
                    <div className="h-16 w-16 bg-white rounded-full flex items-center justify-center mx-auto mb-6 shadow-sm border border-[#E1E1E1]">
                       <Package className="h-8 w-8 text-[#242528]/30" />
                    </div>
                    <h3 className="text-2xl font-heading font-bold text-[#242528] uppercase tracking-tight">No se encontraron artefactos</h3>
                    <p className="text-[#242528]/60 font-sans mt-2">Intenta con otros t├⌐rminos o categor├¡as.</p>
                </div>
            ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                   {filteredProducts.map((product: Producto) => (
                        <ProductCard 
                            key={product.id} 
                            product={product} 
                            onAddToCart={(p) => addToCart(p)} 
                        />
                   ))}
                </div>
            )}
        </div>
      </main>
    </div>
  )
}

"use client"

import { useSearchParams } from "next/navigation"
import { Badge } from "@/components/ui/badge"
import Link from "next/link"
import { Search, BookOpen, User, Shield, Sparkles, ChevronRight } from "lucide-react"
import { Suspense } from "react"

// Tipos para los resultados
export type SearchResultItem = {
    name: string
    description: string
    type: string
    href: string
    id: string
}

interface WikiSearchClientProps {
    initialResults: SearchResultItem[]
    query: string
}

export function WikiSearchClient({ initialResults, query }: WikiSearchClientProps) {
  return (
    <Suspense fallback={<div className="py-20 text-center">Cargando resultados...</div>}>
      <SearchResultsContent results={initialResults} query={query} />
    </Suspense>
  )
}

function SearchResultsContent({ results, query }: { results: SearchResultItem[], query: string }) {
  // Nota: Ya no necesitamos useEffect para buscar hechizos, vienen del servidor.
  // Mantenemos useSearchParams solo si necesitamos reaccionar a cambios en el cliente sin recarga,
  // pero Next.js Server Components manejan esto via navegaci├│n.

  return (
    <div className="max-w-7xl mx-auto space-y-12 pb-20">
      {/* Header Section */}
      <div className="space-y-6 pt-8">
        <h1 className="font-heading font-bold text-5xl uppercase tracking-tight text-[#242528]">
          Resultados de B├║squeda
        </h1>
        <div className="h-[3px] w-48 bg-[#EE8600]" />
        <p className="text-xl text-[#242528] font-sans max-w-3xl leading-relaxed">
          {query ? (
            <>Mostrando resultados para <span className="font-bold text-[#EE8600]">"{query}"</span></>
          ) : (
            "Explora la wiki de La Taberna"
          )}
        </p>
      </div>

      {/* Results Grid */}
      <div className="grid gap-4">
        {results.length > 0 ? (
          results.map((result, i) => (
            <Link 
              key={`${result.type}-${result.id}-${i}`} 
              href={result.href}
              className="group bg-white border border-[#E1E1E1] hover:border-[#EE8600] p-6 flex items-center gap-6 transition-all shadow-sm hover:shadow-md"
            >
              <div className="h-12 w-12 bg-[#F9F9F9] border border-[#E1E1E1] flex items-center justify-center shrink-0 group-hover:bg-[#242528] group-hover:border-[#242528] transition-colors">
                {result.type === "Raza" && <User className="h-6 w-6 text-[#242528] group-hover:text-[#EE8600]" />}
                {result.type === "Clase" && <Shield className="h-6 w-6 text-[#242528] group-hover:text-[#EE8600]" />}
                {result.type === "Trasfondo" && <BookOpen className="h-6 w-6 text-[#242528] group-hover:text-[#EE8600]" />}
                {result.type === "Hechizo" && <Sparkles className="h-6 w-6 text-[#242528] group-hover:text-[#EE8600]" />}
              </div>

              <div className="flex-1 space-y-1">
                <div className="flex items-center gap-3">
                  <h3 className="font-heading text-2xl font-bold uppercase tracking-tight text-[#242528] group-hover:text-[#EE8600] transition-colors">
                    {result.name}
                  </h3>
                  <Badge variant="outline" className="border-[#E1E1E1] text-[#242528]/40 text-[9px] font-bold uppercase tracking-widest rounded-none">
                    {result.type}
                  </Badge>
                </div>
                <p className="text-sm text-[#242528]/60 line-clamp-1 font-sans italic">
                  {result.description}
                </p>
              </div>

              <div className="text-[#E1E1E1] group-hover:text-[#EE8600] transition-colors">
                <ChevronRight className="h-6 w-6" />
              </div>
            </Link>
          ))
        ) : (
          <div className="py-32 text-center space-y-6 bg-white border border-[#E1E1E1]">
            <Search className="h-16 w-16 text-[#E1E1E1] mx-auto" />
            <div className="space-y-2">
              <p className="font-heading text-2xl font-bold uppercase text-[#242528]">No se encontr├│ ning├║n rastro</p>
              <p className="text-sm text-[#242528]/40 font-sans">Prueba con otros t├⌐rminos de b├║squeda como "Elfo", "Mago" o "Bola de Fuego".</p>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}
